ysqli_prepare($database, $sql);","        mysqli_stmt_bind_param($statement, 'ss', $_POST['add_book_title'], $image_path);","        mysqli_stmt_execute($statement);","        mysqli_stmt_close($statement);","    }","    ","    // ステータス変更の処理","    if ($_POST['submit_book_unread']) {","        // 未読へ変更","        $sql = 'UPDATE books SET status=\"unread\" WHERE id=?';       // 実行するSQLを作成","        $statement = mysqli_prepare($database, $sql);                // セキュリティ対策をする","        mysqli_stmt_bind_param($statement, 'i', $_POST['book_id']);  // id=?���?の部分に代入する","        mysqli_stmt_execute($statement);                             // SQL文を実行する","        mysqli_stmt_close($statement);                               // SQL文を破棄する","    }","    elseif ($_POST['submit_book_reading']) {","        // 読中へ変更","        $sql = 'UPDATE books SET status=\"reading\" WHERE id=?';","        $statement = mysqli_prepare($database, $sql);","        mysqli_stmt_bind_param($statement, 'i', $_POST['book_id']);","        mysqli_stmt_execute($statement);","        mysqli_stmt_close($statement);","    }","    elseif ($_POST['submit_book_finished']) {","        // 読了へ変更","        $sql = 'UPDATE books SET status=\"finished\" WHERE id=?';","        $statement = mysqli_prepare($database, $sql);","        mysqli_stmt_bind_param($statement, 'i', $_POST['book_id']);","        mysqli_stmt_execute($statement);","        mysqli_stmt_close($statement);","    }","","    // 未読数のカウント","    $sql = 'SELECT COUNT(*) as count FROM books where status = \"unread\"';","    $result = mysqli_query($database, $sql);","    $record = mysqli_fetch_assoc($result);","    $count_unread = $record['count'];","    // 読中数のカウント","    $sql = 'SELECT COUNT(*) as count FROM books where status = \"reading\"';","    $result = mysqli_query($database, $sql);","    $record = mysqli_fetch_assoc($result);","    $count_reading = $record['count'];","    // 読了数のカウント","    $sql = 'SELECT COUNT(*) as count FROM books where status = \"finished\"';","    $result = mysqli_query($database, $sql);","    $record = mysqli_fetch_assoc($result);","    $count_finished = $record['count'];","","    // どれも$resultにレコードが代入されるので、どの場合でも$resultを使う","    if ($_POST['submit_only_unread']) {","        // 未読ステータスの書籍だけを取得する","        $sql = 'SELECT * FROM books WHERE status=\"unread\" ORDER BY created_at DESC';","    }","    elseif ($_POST['submit_only_reading']) {","        // 読中ステータスの書籍だけを取得する","        $sql = 'SELECT * FROM books WHERE status=\"reading\" ORDER BY created_at DESC';","    }","    elseif ($_POST['submit_only_finished']) {","        // 読了ステータスの書籍だけを取得する","        $sql = 'SELECT * FROM books WHERE status=\"finished\" ORDER BY created_at DESC';","    }","    else {","        // 登録されている書籍を全て取得する","        $sql = 'SELECT * FROM books ORDER BY created_at DESC';","    }","    // if-elseif-else 文なので、 $sql には必ず上記いずれかの SQL 文が入る","    // いずれかの $sql を実行して $result に代入する","    $result = mysqli_query($database, $sql);","    ","    // MySQLを使った処理が終わると、接続は不要なので切断する","    mysqli_close($database);","","?>","<!DOCTYPE html>","<html lang=\"ja\">","    <head>","        <meta charset=\"utf-8\">","        <title>Bookshelf | カンタン！あなたのオンライン本棚</title>","        <link rel=\"stylesheet\" href=\"bookshelf.css\">","    </head>","    <body>","        <header>","            <div id=\"header\">","                <div id=\"logo\">","                    <a href=\"./bookshelf_index.php\"><img src=\"./images/logo.png\" alt=\"Bookshelf\"></a>","                </div>","                <nav>","                    <a href=\"./bookshelf_form.php\"><img src=\"./images/icon_plus.png\" alt=\"\"> 書籍登録</a>","                </nav>","            </div>","        </header>","        <div id=\"cover\">","            <h1 id=\"cover_title\">カンタン！あなたのオンライン本棚</h1>","            <form action=\"bookshelf_index.php\" method=\"post\">","                <div class=\"book_status unread active\">","                    <input type=\"submit\" name=\"submit_only_unread\" 